function badger(e){let l="";for(let t of e)l+='<span class="c-badge">'+t+"</span> ";return l}new Promise(((e,l)=>{projects?e():l("Filters element do not exist")})).then((e=>{for(let e of projects)e.isVisible&&(document.getElementById("grid").innerHTML+='<article class="c-cell '+e.filters+'">        <header class="c-cell__header">          <h3 class="c-cell__title">            <a class="js-link--content c-cell__link"            href="'+e.href+'"            aria-label="'+e.label+'"            data-template="'+e.templatePath+'"            target="_top">'+e.name+'</a>          </h3>        </header>        <figure class="c-cell__fig">          <img src="'+e.img+'" alt="'+e.imgAlt+'" class="c-cell__img">        </figure>        <div class="c-cell__info">          <p>'+badger(e.themes)+"</p>          <p>For: "+e.beneficiary+"</p>          <p>"+e.desc+"</p>        </div>      </article>")}),(e=>{})).finally((()=>moduleRouter.linksListener("js-link--content")));