const moduleCommands=(()=>{function t(t){Array.from(document.getElementsByClassName(t)).forEach((t=>{t.addEventListener("click",(function(t){switch(this.dataset.command){case"back":history.back();break;case"forward":history.forward();break;case"top":isIE()?window.scrollTo(0):window.scrollTo({top:0,behavior:"smooth"});break;case"tint":"light"===localStorage.getItem("theme")?(localStorage.setItem("theme","dark"),wrapBody.classList.add("t-dark")):(localStorage.setItem("theme","light"),wrapBody.classList.remove("t-dark"));break;case"textsize":!function(){switch(localStorage.getItem("font")){case"normal":localStorage.setItem("font","big"),wrapBody.classList.add("t-fontbig"),fontSizeCount.innerHTML="x2";break;case"big":localStorage.setItem("font","bigger"),wrapBody.classList.remove("t-fontbig"),wrapBody.classList.add("t-fontbigger"),fontSizeCount.innerHTML="x3";break;case"bigger":localStorage.setItem("font","normal"),wrapBody.classList.remove("t-fontbigger"),fontSizeCount.innerHTML="x1";break;default:}}();break;default:}}),!1)}))}return{commandsListener:function(e){t(e)},settingsGetter:function(t,e){!function(t,e){if(t)switch(localStorage.getItem("theme")){case"dark":wrapBody.classList.add("t-dark");break;default:}else localStorage.setItem("theme","light");if(e)switch(localStorage.getItem("font")){case"big":wrapBody.classList.add("t-fontbig"),fontSizeCount.innerHTML="x2";break;case"bigger":wrapBody.classList.add("t-fontbigger"),fontSizeCount.innerHTML="x3";break;default:}else localStorage.setItem("font","normal"),fontSizeCount.innerHTML="x1"}(t,e)}}})();